const offset = 10
const lineWidth = 1
const cornerSize = 16
const baseColor = '136, 85, 255'
const color = `rgb(${baseColor})`

export const box = {
  display: 'grid',
  height: '100%',
  gridTemplateColumns: 'repeat(3, 1fr)',
  alignItems: 'center',
  overflow: 'hidden',

  background: `rgba(${baseColor}, 0.1)`,
  color: color,
  border: lineWidth + 'px solid',
}

export const text = {
  gridColumn: 2,
  fontSize: 12,
  textAlign: 'center',
}

export const arrow = {
  padding: 8,
  fontSize: 15,
  textAlign: 'right',
}

const corner = {
  position: 'absolute',
  width: cornerSize,
  height: cornerSize,

  borderStyle: 'solid',
  borderWidth: 0,
}

export const bottomLeft = {
  ...corner,
  bottom: -offset,
  left: -offset,
  borderBottomWidth: lineWidth,
  borderLeftWidth: lineWidth,
}

export const topRight = {
  ...corner,
  top: -offset,
  right: -offset,
  borderTopWidth: lineWidth,
  borderRightWidth: lineWidth,
}
